package snapshot_reader

import (
	"bytes"
	"encoding/hex"
	"testing"
)

func TestNextPossibleKey(t *testing.T) {
	key := "key0"
	nextKey := NextPossibleKey([]byte(key), DirectionForward)
	if hex.EncodeToString(nextKey) != "" || bytes.Compare([]byte(key), nextKey) > 0 {
		t.Fatal("incorrect increment", nextKey)
	}

	prevKey := NextPossibleKey([]byte(key), DirectionReverse)
	if hex.EncodeToString(prevKey) != "" || bytes.Compare([]byte(key), prevKey) < 0 {
		t.Fatal("incorrect decrement")
	}
}
